// Take text before and after typing, and the list of things to type
import React from 'react';

class AutoType extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      before: props.before,
      typedWords: props.typedWords,
      current: '',
      after: props.after,
      deleting: false,
      period: 2000,
      loopNum: 0
    };
  }

  render() {
    this.update();
    return <p>{this.state.before}<b>{this.state.current}</b>{this.state.after}</p>;
  }

  updateText() {
    var i = this.state.loopNum % this.state.typedWords.length;
    var fullText = this.typedWords[i];

    if(this.state.deleting) {
      this.state.current = fullText.substring(0, this.state.current.length - 1)
    } else {
      this.state.current = fullText.substring(0, this.state.current.length + 1)
    }
    var delta = 200 - Math.random() * 100;
    if (this.state.deleting) {
      delta /= 2;
    }
    if (!this.state.deleting && this.state.current === fullText) {
      delta = this.state.period;
      this.state.deleting = true;
    } else if (this.state.deleting && this.state.current === '') {
      this.state.deleting = false;
      this.state.loopNum++;
      delta = 500;
    }
    setTimeout(function() {
      this.update();
    }, delta);
  }

}

TxtType.prototype.tick = function() {

  if (!this.isDeleting && this.txt === fullTxt) {
    delta = this.period;
    this.isDeleting = true;
  } else if (this.isDeleting && this.txt === '') {
    this.isDeleting = false;
    this.loopNum++;
    delta = 500;
  }

  setTimeout(function() {
    that.tick();
  }, delta);
};

window.onload = function() {
  var elements = document.getElementsByClassName('typewrite');
  for (var i = 0; i < elements.length; i++) {
    var toRotate = elements[i].getAttribute('data-type');
    var period = elements[i].getAttribute('data-period');
    if (toRotate) {
      new TxtType(elements[i], JSON.parse(toRotate), period);
    }
  }
  // For Typing Cursor
  // var css = document.createElement("style");
  // css.type = "text/css";
  // css.innerHTML = ".typewrite > .wrap { border-right: 0.08em solid #fff}";
  // document.body.appendChild(css);
};

export default AutoType;
