import React from 'react';
import './App.css';

function ImageText(props) {
  let className = "banner";
  if(props.dark) {
    className = className + " dark";
  }
  let btnBar = "";
  if(props.btnBar) {
    btnBar = (
      <div>
        <a role="button" className="btn btn-outline-primary" href="https://twitter.com/RealRainbowMike" target="_blank" rel="noopener noreferrer"><i class="fab fa-twitter"></i></a>
        <a role="button" class="btn btn-outline-primary" href="https://github.com/MichaelInAction" target="_blank" rel="noopener noreferrer"><i class="fab fa-github"></i></a>
        <a role="button" class="btn btn-outline-primary" href="https://www.linkedin.com/in/mread15/" target="_blank" rel="noopener noreferrer"><i class="fab fa-linkedin-in"></i></a>
        <a role="button" class="btn btn-primary" href="MichaelReadResume.pdf" download="MichaelReadResume" target="_blank" rel="noopener noreferrer">Resume</a>
      </div>);
  }
  return (
    <div class="banner-container">
      <img src={props.img} alt="" class={className}/>
      <div  class="centered">
        <p style={{margin: '-10px'}}>{props.first}</p>
        <h1>{props.head}</h1>
        <p>{props.last}</p>
          {btnBar}
      </div>
    </div>
  );
}

export default ImageText;
