import React from 'react';
import bg1 from './images/banner.jpg';
import './App.css';

function ImageText(props) {
  let className = "centered";
  if(props.dark) {
    className = className + " dark";
  }
  return (
    <div class="banner-container">
      <img src={bg1} alt="" class="banner"/>
      <h1 class={className}>{props.text}</h1>
    </div>
  );
}

export default ImageText;
