import React from 'react';
import bg1 from './images/banner.jpg';
import './App.css';

function ImageText(props) {
  let className = "banner";
  if(props.dark) {
    className = className + " dark";
  }
  return (
    <div class="banner-container">
      <img src={bg1} alt="" class={className}/>
      <div  class="centered">
        <p>{props.first}</p>
        <h1>{props.text}</h1>
        <p>{props.last}</p>
      </div>
    </div>
  );
}

export default ImageText;
